import React, { useState } from 'react';

const Login = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const isEmailValid = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const isPasswordValid = (password) => {
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/;
    return passwordRegex.test(password);
  };

  const handleSubmit = () => {
    let isValid = true;

    // Validar el campo de correo electrónico
    if (!email || !isEmailValid(email)) {
      setEmailError('Por favor, introduzca un correo electrónico válido.');
      isValid = false;
    } else {
      setEmailError('');
    }

     // Validar el campo de contraseña
     if (!isPasswordValid(password)) {
        setPasswordError('La contraseña debe tener al menos 8 caracteres, una letra, un número y un carácter especial.');
        isValid = false;
      } else {
        setPasswordError('');
      }

          // Validar el campo de contraseña
    if (!password) {
        setPasswordError('Por favor, introduzca una contraseña.');
        isValid = false;
      } 

    if (isValid) {
      onLoginSuccess('menu');
    }
  };

  return (
    <body class="bg-login">
    <div class="container">
      <form>
        <div class="formlg">
          <div>
            <label class="item1" htmlFor="email">
              Usuario
            </label>
          </div>
          <div>
            <input
              class="item2"
              type="email"
              id="email"
              name="email"
              placeholder="Correo Electrónico"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {emailError && <p class="error-form">{emailError}</p>}
          </div>
        </div>

        <div class="formlg">
          <div class="grid-item">
            <label class="item1" htmlFor="password">
              Contraseña
            </label>
            <input
              class="item2"
              type="password"
              id="password"
              name="password"
              placeholder="********"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {passwordError && <p class="error-form">{passwordError}</p>}
          </div>
          <div>

          </div>
        </div>

        <div class="btn-ingresar">
          <button type="button" class="btn" onClick={handleSubmit}>
            Ingresar
          </button>
        </div>

        <div class="btn-ingresar">
          <button type="button" class="btn">
            Crear cuenta
          </button>
        </div>
      </form>
    </div>
    </body>
  );
};

export default Login;
