import React, { useState } from 'react';

const Menu = () => {
    const [submenuVisible, setSubmenuVisible] = useState(false);

    const toggleSubmenu = (e) => {
        e.stopPropagation();
        setSubmenuVisible(!submenuVisible);
    };

    const [submenuVisible2, setSubmenuVisible2] = useState(false);

    const toggleSubmenu2 = (e) => {
        e.stopPropagation();

        setSubmenuVisible2(!submenuVisible2);
    };

    return (
        <body class="bg-menu">
            <div class="container">
                <nav class="menu-lateral">
                    <ul>
                        <li class="item-menu" onClick={toggleSubmenu}>
                            Dashboard
                            {submenuVisible && (
                                <ul class="submenu">
                                   <li class="item-menu" onClick={toggleSubmenu2}>Dashboard 1.1</li>
                                    {submenuVisible2 && (
                                    <ul class="submenu">
                                        <li>Subitem1.1.1</li>
                                    </ul>
                                    )}
                                </ul>
                            )}
                        </li>
                    </ul>
                </nav>
            </div>
        </body>
    );
};

export default Menu;
