import React from 'react';
import logo from '../images/logo_evitra.png';

function Header({ onScreenChange, isMenu }) {
  const handleNavClick = (screen) => {
    onScreenChange(screen);
  };

  if (isMenu) {
    return (
      <header className="alternate-header">
        {/* Contenido específico del encabezado del menú */}
        <h1>Menú</h1>
        <button onClick={() => handleNavClick('inicio')}>Ir a Inicio</button>
      </header>
    );
  }

  // Encabezado predeterminado
  return (
    <header className="navbar">
      <nav>
        <ul className="menu">
          <li className="iniciar">
            <a href='#' onClick={() => handleNavClick('login')}>Iniciar sesión</a>
          </li>
        </ul>
      </nav>
      <img src={logo} alt="logo" className="img-logo" />
    </header>
  );
}

export default Header;
