import React, { useState } from 'react';
import '../styleApp.css'; // Importa el archivo CSS

function Menu() {
  const [submenuVisible, setSubmenuVisible] = useState(false);

  const toggleSubmenu = (submenuId) => {
    setSubmenuVisible(!submenuVisible);
  };

  return (

      <body class="bg-menu">
        <div class="container">
          <nav class="menu-lateral">
            <ul>
              <li class="item-menu" onClick={() => toggleSubmenu('submenu1')}>
                Dashboard
              </li>
              <ul
                id="submenu1"
                class={`submenu ${submenuVisible ? 'show' : ''}`}
              >
                <li class="item-menu" onClick={() => toggleSubmenu('submenu1-1')}>
                  Dashboard 1.1
                </li>
                <ul id="submenu1-1" class="submenu">
                  <li>Subitem1.1.1</li>
                </ul>
              </ul>
            </ul>
          </nav>
        </div>
      </body>
  );
}

export default App;
