import React, { useState } from 'react';
import './styleApp.css';
import Footer from './components/FooterForm';
import Header from './components/HeaderForm';
import Inicio from './components/Inicio';
import Login from './components/LoginForm'; // Importa el componente de Login
import Menu from './components/Menu';

function App() {
  const [currentScreen, setCurrentScreen] = useState('inicio');
  const [headerType, setHeaderType] = useState('normal'); // Estado para controlar el tipo de encabezado

  const handleScreenChange = (screen) => {
    setCurrentScreen(screen);
    if (screen === 'menu') {
      setHeaderType('menu'); // Cambia el tipo de encabezado cuando estás en la pantalla del menú
    } else {
      setHeaderType('normal'); // Vuelve al encabezado normal para otras pantallas
    }
  };

  const handleLoginSuccess = (screen) => {
    setCurrentScreen(screen); // Cambia a la pantalla indicada al iniciar sesión correctamente
  };

 let screenToShow;
  if (currentScreen === 'inicio') {
    screenToShow = <Inicio />;
  } else if (currentScreen === 'login') {
    screenToShow = <Login onLoginSuccess={handleLoginSuccess} />;
  } else if (currentScreen === 'menu') {
    screenToShow = <Menu />;
  }

  let headerToShow;
  if (headerType === 'normal') {
    headerToShow = <Header onScreenChange={handleScreenChange} isMenu={false} />;
  } else if (headerType === 'menu') {
    headerToShow = <Header onScreenChange={handleScreenChange} isMenu={true} />;
  }


  return (
    <div>
      {headerToShow}
      {screenToShow}
      <Footer />
    </div>
  );
}

export default App;
