import React, { useState } from 'react';

const Login = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const isEmailValid = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = () => {
    let isValid = true;

    // Validar el campo de correo electrónico
    if (!email || !isEmailValid(email)) {
      setEmailError('Por favor, introduzca un correo electrónico válido.');
      isValid = false;
    } else {
      setEmailError('');
    }

    // Validar el campo de contraseña
    if (!password) {
      setPasswordError('Por favor, introduzca una contraseña.');
      isValid = false;
    } else {
      setPasswordError('');
    }

    if (isValid && email === password) {
      onLoginSuccess('menu');
    }
  };

  return (
    <body class="container">
      <form>
        <div class="formlg">
          <div>
            <label class="item1" htmlFor="email">
              Usuario
            </label>
          </div>
          <div>
            <input
              class="item2"
              type="email"
              id="email"
              name="email"
              placeholder="Correo Electrónico"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {emailError && <p class="error-form">{emailError}</p>}
          </div>
        </div>

        <div class="formlg">
          <div>
            <label class="item1" htmlFor="password">
              Contraseña
            </label>
          </div>
          <div>
            <input
              class="item2"
              type="password"
              id="password"
              name="password"
              placeholder="Contraseña"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {passwordError && <p class="error-form">{passwordError}</p>}
          </div>
        </div>

        <div class="btn-ingresar">
          <button type="button" class="btn" onClick={handleSubmit}>
            Ingresar
          </button>
        </div>

        <div class="btn-ingresar">
          <button type="button" class="btn">
            Crear cuenta
          </button>
        </div>
      </form>
    </body>
  );
};

export default Login;
