import React, {useState} from 'react'

const Login = ({onLoginSuccess}) => {

    const [email,setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState({});

    
    //Expresión para evaluar el formato del correo electrónico.
    const isEmailValid = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    const handleSubmit = (e) =>{
        e.preventDefault();


        //Validar campos vacíos.
        if(!email || !password){
            setError('Por favor, complete los campos.');
            return;
        }

        //Validar formato de correo
        if(!isEmailValid(email)){
            setError('Por favor, introduzca un correo electrónico válido.');
            return;
        }

        console.log({email,password});
        //Enviar datos al servidor

        //Formatea los errores
        setError('');
    };


    return (

<body class="bg-login">
<div class="container">
    <form onSubmit={handleSubmit}>
        <div class='formlg'>
        <div>
            <label class="item1" for="email">Usuario</label>
        </div>
        <div>
            <input class="item2" type="email" id="email" name="email" placeholder="Correo Electrónico" required value={email} onChange={(e) => setEmail(e.target.value)}/>
        </div>
        </div>
      
        <div class='formlg'>
        <div>
            <label class="item1" for="password">Contraseña</label>
        </div>
        <div>
            <input class="item2" type="password" id="password" name="password" placeholder="Contraseña" required  value={password} onChange={(e) => setPassword(e.target.value)}/>
        </div>
    </div>
    {error && <p class="error-form">(error)</p>}


    <a href="#" class="">Olvidé mi contraseña</a>
   
        <div class="btn-ingresar">
            <button type="submit" class="btn" onClick={handleSubmit}>Ingresar</button>
        </div>
        
        <div class="btn-ingresar">
            <button type="submit" class="btn">Crear cuenta</button>
        </div>
        </form>
    
</div>
</body>
)}

export default Login
